mapper.select:
SELECT DISTINCT i.requestForQuotationId, i.createTime, i.startTime, i.endTime, i.status inquiryStatus,i.createName, l.legalPersonName
FROM inquiry i
LEFT JOIN legalperson l ON i.legalPersonId = l.id
LEFT JOIN inquiry_supplier ins ON i.requestForQuotationId = ins.requestForQuotationId


		and i.legalPersonId = #{legalPersonId}


		and i.requestForQuotationId like concat('%',#{requestForQuotationId},'%')


		and ins.supplierCode = #{supplierCode}


		and i.type = #{type}


		and i.status = #{inquiryStatus}


		and i.createTime >= #{createStartTime}


		and  i.createTime <= #{createEndTime}


		and i.startTime >= #{startTime}


		and  i.endTime <= #{endTime}


order by createTime desc
limit #{offset}, #{pageSize}

SELECT count(DISTINCT i.id)
FROM inquiry i
LEFT JOIN legalperson l ON i.legalPersonId = l.id
LEFT JOIN inquiry_supplier ins ON i.requestForQuotationId = ins.requestForQuotationId


		and i.legalPersonId = #{legalPersonId}


		and i.requestForQuotationId like concat('%',#{requestForQuotationId},'%')


		and ins.supplierCode = #{supplierCode}


		and i.type = #{type}


		and i.status = #{inquiryStatus}


		and i.createTime >= #{createStartTime}


		and  i.createTime <= #{createEndTime}


		and i.startTime >= #{startTime}


		and  i.endTime <= #{endTime}
