mapper.select:
SELECT sb.id, sb.status, sb.code, sb.name, s.id storageSectionId, s.code storageSectionCode, s.name storageSectionName, s.remark storageSectionRemark, w.id wareHouseId, w.name wareHouseName, w.code wareHouseCode, w.remark wareHouseRemark FROM storagebin sb LEFT JOIN storagesection s ON sb.storageSectionCode = s.code LEFT JOIN warehouse w ON w.code = s.wareHouseCode
and sb.code like concat("%",#{code},"%")
and storageSectionCode like concat("%",#{storageSectionCode},"%")
and wareHouseCode like concat("%",#{wareHouseCode},"%")
and sb.status = #{status}
order by sb.createTime desc limit #{offset},#{pageSize}
